import { SAVE_EMAIL, SAVE_IMG } from '../actions/index';

const INITIAL_STATE = {
  email: '',
  img: '',
};

const userReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case SAVE_EMAIL:
    return { ...state, email: action.payload };
  default:
    return state;
  }
};

export default userReducer;
