import SAVE_EMAIL from '../actions/';

const INITIAL_STATE = {
  email: '',
  password: '',
}

const userReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case SAVE_EMAIL: 
      return { ...state, email: action.payload }
  }
} 

export default userReducer;
